mapper.select:
SELECT id, blog_id, userId, nickName, title, created_at, liked_count, comment_count FROM post WHERE deleted_at IS NULL
AND (title LIKE CONCAT ('%', #{keyword}, '%') OR content LIKE CONCAT ('%', #{keyword}, '%') OR nickName LIKE CONCAT ('%', #{keyword}, '%'))
ORDER BY id DESC LIMIT #{pagination.limitStart}, #{recordSize}
